import React, { useState } from "react";
import "./styles/Game.css";
import { GameState, Word, GameStats, ExerciseType } from "./types/game";
import DefinitionMatch from "./components/DefinitionMatch";
import SynonymExercise from "./components/SynonymExercise";
import AntonymExercise from "./components/AntonymExercise";
import MemoryGame from "./components/MemoryGame";

const initialStats: GameStats = {
  correctAnswers: 0,
  totalQuestions: 0,
  currentStreak: 0,
  bestStreak: 0,
};

const initialGameState: GameState = {
  currentExercise: null,
  stats: initialStats,
  progress: 0,
  selectedWords: [],
  matchedPairs: new Set(),
};

// Sample vocabulary data
const vocabularyData: Word[] = [
  {
    id: 1,
    word: "Ephemeral",
    definition: "Lasting for a very short time",
    synonyms: ["Transient", "Fleeting", "Brief"],
    antonyms: ["Permanent", "Enduring", "Lasting"],
  },
  {
    id: 2,
    word: "Ubiquitous",
    definition: "Present, appearing, or found everywhere",
    synonyms: ["Omnipresent", "Pervasive", "Universal"],
    antonyms: ["Rare", "Scarce", "Limited"],
  },
  {
    id: 3,
    word: "Eloquent",
    definition: "Fluent or persuasive in speaking or writing",
    synonyms: ["Articulate", "Expressive", "Fluent"],
    antonyms: ["Inarticulate", "Mute", "Tongue-tied"],
  },
  {
    id: 4,
    word: "Pragmatic",
    definition: "Dealing with things sensibly and realistically",
    synonyms: ["Practical", "Realistic", "Sensible"],
    antonyms: ["Idealistic", "Impractical", "Unrealistic"],
  },
  {
    id: 5,
    word: "Ambiguous",
    definition: "Open to more than one interpretation",
    synonyms: ["Unclear", "Vague", "Uncertain"],
    antonyms: ["Clear", "Definite", "Unambiguous"],
  },
];

const App: React.FC = () => {
  const [gameState, setGameState] = useState<GameState>(initialGameState);

  const startExercise = (type: ExerciseType) => {
    setGameState((prev) => ({
      ...prev,
      currentExercise: type,
      progress: 0,
      selectedWords: vocabularyData.slice(0, 5),
      matchedPairs: new Set(),
    }));
  };

  const updateStats = (isCorrect: boolean) => {
    setGameState((prev) => {
      const newStats = { ...prev.stats };
      newStats.totalQuestions++;

      if (isCorrect) {
        newStats.correctAnswers++;
        newStats.currentStreak++;
        newStats.bestStreak = Math.max(
          newStats.bestStreak,
          newStats.currentStreak
        );
      } else {
        newStats.currentStreak = 0;
      }

      return {
        ...prev,
        stats: newStats,
      };
    });
  };

  const renderExercise = () => {
    switch (gameState.currentExercise) {
      case "definition":
        return (
          <DefinitionMatch
            words={gameState.selectedWords}
            onAnswer={updateStats}
          />
        );
      case "synonym":
        return (
          <SynonymExercise
            words={gameState.selectedWords}
            onAnswer={updateStats}
          />
        );
      case "antonym":
        return (
          <AntonymExercise
            words={gameState.selectedWords}
            onAnswer={updateStats}
          />
        );
      case "memory":
        return (
          <MemoryGame words={gameState.selectedWords} onAnswer={updateStats} />
        );
      default:
        return null;
    }
  };

  return (
    <div className="game-container">
      <div className="header">
        <h1 className="title">Vocabulary Master Game</h1>
        <div className="stats">
          <div className="stat">
            <div className="stat-value">{gameState.stats.correctAnswers}</div>
            <div className="stat-label">Correct</div>
          </div>
          <div className="stat">
            <div className="stat-value">{gameState.stats.currentStreak}</div>
            <div className="stat-label">Current Streak</div>
          </div>
          <div className="stat">
            <div className="stat-value">{gameState.stats.bestStreak}</div>
            <div className="stat-label">Best Streak</div>
          </div>
        </div>
      </div>

      {!gameState.currentExercise ? (
        <div className="game-menu">
          <button
            className="game-btn"
            onClick={() => startExercise("definition")}
          >
            Definition Match
          </button>
          <button className="game-btn" onClick={() => startExercise("synonym")}>
            Synonym Exercise
          </button>
          <button className="game-btn" onClick={() => startExercise("antonym")}>
            Antonym Exercise
          </button>
          <button className="game-btn" onClick={() => startExercise("memory")}>
            Memory Game
          </button>
        </div>
      ) : (
        <div className="exercise active">
          <h2 className="exercise-title">
            {gameState.currentExercise.charAt(0).toUpperCase() +
              gameState.currentExercise.slice(1)}{" "}
            Exercise
          </h2>
          <div className="progress-bar">
            <div
              className="progress-fill"
              style={{
                width: `${
                  (gameState.progress / gameState.selectedWords.length) * 100
                }%`,
              }}
            />
          </div>
          {renderExercise()}
        </div>
      )}
    </div>
  );
};

export default App;
