import React, { useState, useEffect } from "react";
import { Word } from "../types/game";

interface MemoryGameProps {
  words: Word[];
  onAnswer: (isCorrect: boolean) => void;
}

interface Card {
  id: number;
  content: string;
  type: "word" | "definition";
  isFlipped: boolean;
  isMatched: boolean;
}

const MemoryGame: React.FC<MemoryGameProps> = ({ words, onAnswer }) => {
  const [cards, setCards] = useState<Card[]>([]);
  const [flippedCards, setFlippedCards] = useState<number[]>([]);
  const [matchedPairs, setMatchedPairs] = useState<Set<number>>(new Set());

  useEffect(() => {
    // Create cards from words and their definitions
    const newCards: Card[] = [
      ...words.map((word) => ({
        id: word.id,
        content: word.word,
        type: "word" as const,
        isFlipped: false,
        isMatched: false,
      })),
      ...words.map((word) => ({
        id: word.id,
        content: word.definition,
        type: "definition" as const,
        isFlipped: false,
        isMatched: false,
      })),
    ];

    // Shuffle the cards
    setCards(newCards.sort(() => Math.random() - 0.5));
  }, [words]);

  const handleCardClick = (index: number) => {
    if (
      flippedCards.length === 2 ||
      cards[index].isMatched ||
      flippedCards.includes(index)
    ) {
      return;
    }

    const newFlippedCards = [...flippedCards, index];
    setFlippedCards(newFlippedCards);

    // Update card flip state
    setCards((prevCards) => {
      const newCards = [...prevCards];
      newCards[index] = { ...newCards[index], isFlipped: true };
      return newCards;
    });

    // Check for match if two cards are flipped
    if (newFlippedCards.length === 2) {
      const [firstIndex, secondIndex] = newFlippedCards;
      const firstCard = cards[firstIndex];
      const secondCard = cards[secondIndex];

      if (firstCard.id === secondCard.id) {
        // Match found
        setMatchedPairs((prev) => new Set([...prev, firstCard.id]));
        onAnswer(true);

        // Update matched state
        setCards((prevCards) => {
          const newCards = [...prevCards];
          newCards[firstIndex] = { ...newCards[firstIndex], isMatched: true };
          newCards[secondIndex] = { ...newCards[secondIndex], isMatched: true };
          return newCards;
        });
      } else {
        // No match
        onAnswer(false);
        setTimeout(() => {
          setCards((prevCards) => {
            const newCards = [...prevCards];
            newCards[firstIndex] = {
              ...newCards[firstIndex],
              isFlipped: false,
            };
            newCards[secondIndex] = {
              ...newCards[secondIndex],
              isFlipped: false,
            };
            return newCards;
          });
        }, 1000);
      }
      setFlippedCards([]);
    }
  };

  return (
    <div className="cards-grid">
      {cards.map((card, index) => (
        <div
          key={`${card.type}-${index}`}
          className={`card ${card.isFlipped ? "flipped" : ""} ${
            card.isMatched ? "matched" : ""
          }`}
          onClick={() => handleCardClick(index)}
        >
          <div className="card-inner">
            <div className="card-front">?</div>
            <div className="card-back">{card.content}</div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default MemoryGame;
