import React, { useState } from "react";
import "./styles/Game.css";
import { GameState, Word, GameStats, ExerciseType } from "./types/game";
import DefinitionMatch from "./components/DefinitionMatch";
import SynonymExercise from "./components/SynonymExercise";
import AntonymExercise from "./components/AntonymExercise";
import MemoryGame from "./components/MemoryGame";

const initialStats: GameStats = {
  correctAnswers: 0,
  totalQuestions: 0,
  currentStreak: 0,
  bestStreak: 0,
};

const initialGameState: GameState = {
  currentExercise: null,
  stats: initialStats,
  progress: 0,
  selectedWords: [],
  matchedPairs: new Set(),
};

// IELTS Vocabulary data from the screenshot
const vocabularyData: Word[] = [
  {
    id: 1,
    word: "Chain reaction",
    definition:
      "A chain reaction is a series of events, each of which causes the next",
    synonyms: ["Sequence", "Series", "Domino effect"],
    antonyms: ["Isolation", "Single event", "Independence"],
  },
  {
    id: 2,
    word: "Consequence",
    definition:
      "The consequences of something are the results or effects of it",
    synonyms: ["Result", "Effect", "Outcome"],
    antonyms: ["Cause", "Origin", "Source"],
  },
  {
    id: 3,
    word: "Impact",
    definition:
      "The impact that something has on a situation, process, or person is a sudden and powerful effect that it has on them",
    synonyms: ["Effect", "Influence", "Force"],
    antonyms: ["Weakness", "Insignificance", "Neglect"],
  },
  {
    id: 4,
    word: "Influence",
    definition:
      "To have an influence on people or situations means to affect what they do or what happens",
    synonyms: ["Affect", "Shape", "Control"],
    antonyms: ["Ignore", "Neglect", "Avoid"],
  },
  {
    id: 5,
    word: "Outcome",
    definition:
      "The outcome of an activity, process, or situation is the situation that exists at the end of it",
    synonyms: ["Result", "End", "Conclusion"],
    antonyms: ["Beginning", "Start", "Cause"],
  },
  {
    id: 6,
    word: "Repercussion",
    definition:
      "If an action or event has repercussions, it causes unpleasant things to happen some time after the original action or event",
    synonyms: ["Consequence", "Effect", "Backlash"],
    antonyms: ["Benefit", "Advantage", "Reward"],
  },
  {
    id: 7,
    word: "Affect",
    definition:
      "If something affects a person or thing, it influences them or causes them to change in some way",
    synonyms: ["Influence", "Impact", "Change"],
    antonyms: ["Ignore", "Preserve", "Maintain"],
  },
  {
    id: 8,
    word: "Contribute",
    definition:
      "If something contributes to an event or situation, it is one of the causes of it",
    synonyms: ["Help", "Add", "Support"],
    antonyms: ["Hinder", "Prevent", "Block"],
  },
  {
    id: 9,
    word: "Determine",
    definition:
      "If a particular factor determines the nature of a thing or event, it causes it to be of a particular kind",
    synonyms: ["Decide", "Control", "Shape"],
    antonyms: ["Ignore", "Neglect", "Randomize"],
  },
  {
    id: 10,
    word: "Generate",
    definition: "To generate something means to cause it to begin and develop",
    synonyms: ["Create", "Produce", "Make"],
    antonyms: ["Destroy", "Stop", "End"],
  },
  {
    id: 11,
    word: "Induce",
    definition: "To induce a state or condition means to cause it",
    synonyms: ["Cause", "Trigger", "Bring about"],
    antonyms: ["Prevent", "Stop", "Block"],
  },
  {
    id: 12,
    word: "Provoke",
    definition: "If something provokes a reaction, it causes it",
    synonyms: ["Trigger", "Cause", "Spark"],
    antonyms: ["Calm", "Soothe", "Prevent"],
  },
  {
    id: 13,
    word: "Result",
    definition:
      "If something results in a particular situation or event, it causes that situation or event to happen",
    synonyms: ["Lead to", "Cause", "Produce"],
    antonyms: ["Prevent", "Block", "Stop"],
  },
  {
    id: 14,
    word: "Stem",
    definition:
      "If a condition or problem stems from something, it was caused originally by that thing",
    synonyms: ["Come from", "Arise", "Originate"],
    antonyms: ["End", "Finish", "Conclude"],
  },
  {
    id: 15,
    word: "Trigger",
    definition:
      "If something triggers an event or situation, it causes it to begin to happen or exist",
    synonyms: ["Cause", "Start", "Set off"],
    antonyms: ["Stop", "Prevent", "End"],
  },
];

const App: React.FC = () => {
  const [gameState, setGameState] = useState<GameState>(initialGameState);

  const startExercise = (type: ExerciseType) => {
    setGameState((prev) => ({
      ...prev,
      currentExercise: type,
      progress: 0,
      selectedWords: vocabularyData.slice(0, 5),
      matchedPairs: new Set(),
    }));
  };

  const updateStats = (isCorrect: boolean) => {
    setGameState((prev) => {
      const newStats = { ...prev.stats };
      newStats.totalQuestions++;

      if (isCorrect) {
        newStats.correctAnswers++;
        newStats.currentStreak++;
        newStats.bestStreak = Math.max(
          newStats.bestStreak,
          newStats.currentStreak
        );
      } else {
        newStats.currentStreak = 0;
      }

      return {
        ...prev,
        stats: newStats,
      };
    });
  };

  const renderExercise = () => {
    switch (gameState.currentExercise) {
      case "definition":
        return (
          <DefinitionMatch
            words={gameState.selectedWords}
            onAnswer={updateStats}
          />
        );
      case "synonym":
        return (
          <SynonymExercise
            words={gameState.selectedWords}
            onAnswer={updateStats}
          />
        );
      case "antonym":
        return (
          <AntonymExercise
            words={gameState.selectedWords}
            onAnswer={updateStats}
          />
        );
      case "memory":
        return (
          <MemoryGame words={gameState.selectedWords} onAnswer={updateStats} />
        );
      default:
        return null;
    }
  };

  return (
    <div className="game-container">
      <div className="header">
        <h1 className="title">Vocabulary Master Game</h1>
        <div className="stats">
          <div className="stat">
            <div className="stat-value">{gameState.stats.correctAnswers}</div>
            <div className="stat-label">Correct</div>
          </div>
          <div className="stat">
            <div className="stat-value">{gameState.stats.currentStreak}</div>
            <div className="stat-label">Current Streak</div>
          </div>
          <div className="stat">
            <div className="stat-value">{gameState.stats.bestStreak}</div>
            <div className="stat-label">Best Streak</div>
          </div>
        </div>
      </div>

      {!gameState.currentExercise ? (
        <div className="game-menu">
          <button
            className="game-btn"
            onClick={() => startExercise("definition")}
          >
            Definition Match
          </button>
          <button className="game-btn" onClick={() => startExercise("synonym")}>
            Synonym Exercise
          </button>
          <button className="game-btn" onClick={() => startExercise("antonym")}>
            Antonym Exercise
          </button>
          <button className="game-btn" onClick={() => startExercise("memory")}>
            Memory Game
          </button>
        </div>
      ) : (
        <div className="exercise active">
          <h2 className="exercise-title">
            {gameState.currentExercise.charAt(0).toUpperCase() +
              gameState.currentExercise.slice(1)}{" "}
            Exercise
          </h2>
          <div className="progress-bar">
            <div
              className="progress-fill"
              style={{
                width: `${
                  (gameState.progress / gameState.selectedWords.length) * 100
                }%`,
              }}
            />
          </div>
          {renderExercise()}
        </div>
      )}
    </div>
  );
};

export default App;
